/bin/sh -c "> /var/log/lxc/lxc.log && (while true; do clear && printf '\e[3J'; tail -n 100 /var/log/lxc/lxc.log > /var/log/lxc/lxc.log.tmp && cat /var/log/lxc/lxc.log.tmp > /var/log/lxc/lxc.log && rm /var/log/lxc/lxc.log.tmp || true; cat /var/log/lxc/lxc.log; sleep 2; done) > /dev/console 2>&1 & bash -c 'alembic upgrade head > /sl/migration.log 2>&1 && python init_app.py > /sl/init.log 2>&1 && python job_runner.py > /sl/job_runner.log 2>&1 & python email_handler.py > /sl/email_handler.log 2>&1 & gunicorn wsgi:app -b 0.0.0.0:7777 -w 2 --timeout 60' >> /var/log/lxc/lxc.log 2>&1 & wait"
